// –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–∞—Ä–æ–ª—è
function validatePassword(password) {
    const missing = [];
    if (!/[A-Za-z]/.test(password)) missing.push('–ª–∞—Ç–∏–Ω—Å–∫–∏–µ –±—É–∫–≤—ã (A-Z / a-z)');
    if (!/[–ê-–Ø–∞-—è–Å—ë]/.test(password)) missing.push('–∫–∏—Ä–∏–ª–ª–∏—á–µ—Å–∫–∏–µ —Å–∏–º–≤–æ–ª—ã (–ê-–Ø / –∞-—è)');
    if (!/[a-z–∞-—è—ë]/.test(password)) missing.push('—Å—Ç—Ä–æ—á–Ω—ã–µ –±—É–∫–≤—ã');
    if (!/[A-Z–ê-–Ø–Å]/.test(password)) missing.push('–ø—Ä–æ–ø–∏—Å–Ω—ã–µ –±—É–∫–≤—ã');
    if (!/\d/.test(password)) missing.push('—Ü–∏—Ñ—Ä—ã (0-9)');
    if (!/[.,;:!?]/.test(password)) missing.push('–∑–Ω–∞–∫–∏ –ø—Ä–µ–ø–∏–Ω–∞–Ω–∏—è');
    if (!/[+\-*/%]/.test(password)) missing.push('–∑–Ω–∞–∫–∏ –∞—Ä–∏—Ñ–º–µ—Ç–∏–∫–∏ (+ - * / %)');
    return missing;
}

// ================= –ê–õ–ì–û–†–ò–¢–ú–´ –®–ò–§–†–û–í–ê–ù–ò–Ø =================

// –ê–ª—Ñ–∞–≤–∏—Ç—ã
const LATIN_LOWER = "abcdefghijklmnopqrstuvwxyz";
const LATIN_UPPER = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
const CYR_LOWER = "–∞–±–≤–≥–¥–µ—ë–∂–∑–∏–π–∫–ª–º–Ω–æ–ø—Ä—Å—Ç—É—Ñ—Ö—Ü—á—à—â—ä—ã—å—ç—é—è";
const CYR_UPPER = "–ê–ë–í–ì–î–ï–Å–ñ–ó–ò–ô–ö–õ–ú–ù–û–ü–†–°–¢–£–§–•–¶–ß–®–©–™–´–¨–≠–Æ–Ø";

// –¶–µ–∑–∞—Ä—å
function caesarCipher(text, shift) {
    let result = '';
    for (let char of text) {
        if (LATIN_LOWER.includes(char)) {
            result += shiftAlphabet(char, LATIN_LOWER, shift);
        } else if (LATIN_UPPER.includes(char)) {
            result += shiftAlphabet(char, LATIN_UPPER, shift);
        } else if (CYR_LOWER.includes(char)) {
            result += shiftAlphabet(char, CYR_LOWER, shift);
        } else if (CYR_UPPER.includes(char)) {
            result += shiftAlphabet(char, CYR_UPPER, shift);
        } else {
            result += char;
        }
    }
    return result;
}

function shiftAlphabet(char, alphabet, shift) {
    const index = alphabet.indexOf(char);
    if (index === -1) return char;
    const newIndex = (index + shift) % alphabet.length;
    return alphabet[newIndex < 0 ? newIndex + alphabet.length : newIndex];
}

// –í–∏–∂–µ–Ω–µ—Ä
function vigenereCipher(text, key, decrypt = false) {
    if (!key) return text;
    let result = '';
    let keyIndex = 0;
    
    for (let char of text) {
        let shift = 0;
        if (LATIN_LOWER.includes(char)) {
            shift = getShiftFromKey(key[keyIndex % key.length], LATIN_LOWER);
            result += shiftAlphabet(char, LATIN_LOWER, decrypt ? -shift : shift);
            keyIndex++;
        } else if (LATIN_UPPER.includes(char)) {
            shift = getShiftFromKey(key[keyIndex % key.length], LATIN_UPPER);
            result += shiftAlphabet(char, LATIN_UPPER, decrypt ? -shift : shift);
            keyIndex++;
        } else if (CYR_LOWER.includes(char)) {
            shift = getShiftFromKey(key[keyIndex % key.length], CYR_LOWER);
            result += shiftAlphabet(char, CYR_LOWER, decrypt ? -shift : shift);
            keyIndex++;
        } else if (CYR_UPPER.includes(char)) {
            shift = getShiftFromKey(key[keyIndex % key.length], CYR_UPPER);
            result += shiftAlphabet(char, CYR_UPPER, decrypt ? -shift : shift);
            keyIndex++;
        } else {
            result += char;
        }
    }
    return result;
}

function getShiftFromKey(keyChar, alphabet) {
    let pos = alphabet.indexOf(keyChar);
    if (pos !== -1) return pos;
    pos = alphabet.indexOf(keyChar.toLowerCase());
    if (pos !== -1) return pos;
    pos = alphabet.indexOf(keyChar.toUpperCase());
    if (pos !== -1) return pos;
    return 0;
}

// XOR
function xorEncrypt(text, key) {
    if (!key) return text;
    const textBytes = new TextEncoder().encode(text);
    const keyBytes = new TextEncoder().encode(key);
    const result = new Uint8Array(textBytes.length);
    
    for (let i = 0; i < textBytes.length; i++) {
        result[i] = textBytes[i] ^ keyBytes[i % keyBytes.length];
    }
    
    return Array.from(result).map(b => b.toString(16).padStart(2, '0')).join('');
}

function xorDecrypt(hexText, key) {
    if (!key) return hexText;
    try {
        const bytes = new Uint8Array(hexText.match(/.{2}/g).map(byte => parseInt(byte, 16)));
        const keyBytes = new TextEncoder().encode(key);
        const result = new Uint8Array(bytes.length);
        
        for (let i = 0; i < bytes.length; i++) {
            result[i] = bytes[i] ^ keyBytes[i % keyBytes.length];
        }
        
        return new TextDecoder().decode(result);
    } catch (e) {
        return '–û—à–∏–±–∫–∞: –æ–∂–∏–¥–∞–µ—Ç—Å—è hex-—Å—Ç—Ä–æ–∫–∞ (0-9a-f)';
    }
}

// –ú–æ–¥—É–ª—å–Ω–æ–µ –≤–æ–∑–≤–µ–¥–µ–Ω–∏–µ –≤ —Å—Ç–µ–ø–µ–Ω—å (–¥–ª—è –±–æ–ª—å—à–∏—Ö —á–∏—Å–µ–ª)
function modPow(base, exp, mod) {
    base = BigInt(base);
    exp = BigInt(exp);
    mod = BigInt(mod);
    let result = 1n;
    base = base % mod;
    while (exp > 0n) {
        if (exp % 2n === 1n) {
            result = (result * base) % mod;
        }
        exp = exp >> 1n;
        base = (base * base) % mod;
    }
    return Number(result);
}

// RSA (–∏—Å–ø—Ä–∞–≤–ª–µ–Ω–Ω–∞—è –≤–µ—Ä—Å–∏—è —Å –º–æ–¥—É–ª—å–Ω—ã–º –≤–æ–∑–≤–µ–¥–µ–Ω–∏–µ–º –≤ —Å—Ç–µ–ø–µ–Ω—å)
function rsaEncrypt(text, e, n) {
    try {
        return text.split('').map(char => {
            const m = char.charCodeAt(0);
            if (m >= n) {
                return `[–ë–õ–û–ö:${m}]`; // –î–ª—è —Å–∏–º–≤–æ–ª–æ–≤ >= n –∏—Å–ø–æ–ª—å–∑—É–µ–º –±–ª–æ–∫–∏—Ä–æ–≤–∞–Ω–∏–µ
            }
            return modPow(m, e, n);
        }).join(' ');
    } catch (error) {
        return `–û—à–∏–±–∫–∞: ${error.message}`;
    }
}

function rsaDecrypt(cipherNumbers, d, n) {
    try {
        const numbers = cipherNumbers.trim().split(/\s+/);
        return numbers.map(num => {
            if (num.startsWith('[–ë–õ–û–ö:')) {
                const blockNum = parseInt(num.match(/\d+/)[0]);
                return String.fromCharCode(blockNum);
            }
            const decrypted = modPow(parseInt(num), d, n);
            return String.fromCharCode(decrypted);
        }).join('');
    } catch (e) {
        return '–û—à–∏–±–∫–∞: –æ–∂–∏–¥–∞–µ—Ç—Å—è –Ω–∞–±–æ—Ä —Ü–µ–ª—ã—Ö —á–∏—Å–µ–ª, —Ä–∞–∑–¥–µ–ª—ë–Ω–Ω—ã—Ö –ø—Ä–æ–±–µ–ª–∞–º–∏';
    }
}

// –ú–æ–¥—É–ª—å–Ω–æ–µ –æ–±—Ä–∞—Ç–Ω–æ–µ (–¥–ª—è ElGamal)
function modInverse(a, m) {
    a = BigInt(a);
    m = BigInt(m);
    a = ((a % m) + m) % m;
    let [oldR, r] = [a, m];
    let [oldS, s] = [1n, 0n];
    
    while (r !== 0n) {
        const quotient = oldR / r;
        [oldR, r] = [r, oldR - quotient * r];
        [oldS, s] = [s, oldS - quotient * s];
    }
    
    if (oldR > 1n) return null;
    return Number(((oldS % m) + m) % m);
}

// ElGamal (–∏—Å–ø—Ä–∞–≤–ª–µ–Ω–Ω–∞—è –≤–µ—Ä—Å–∏—è —Å –±–æ–ª—å—à–∏–º p –∏ –º–æ–¥—É–ª—å–Ω—ã–º –≤–æ–∑–≤–µ–¥–µ–Ω–∏–µ–º)
function elgamalEncrypt(text, p, g, y) {
    if (p <= 2) return '–û—à–∏–±–∫–∞: p –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å –ø—Ä–æ—Å—Ç—ã–º > 2';
    
    const pairs = [];
    
    for (let char of text) {
        const m = char.charCodeAt(0);
        if (m >= p) {
            return `–û—à–∏–±–∫–∞: —Å–∏–º–≤–æ–ª —Å –∫–æ–¥–æ–º ${m} >= p (${p}). –í—ã–±–µ—Ä–∏—Ç–µ –±–æ–ª—å—à–µ–µ p (—Ä–µ–∫–æ–º–µ–Ω–¥—É–µ—Ç—Å—è >= 65537 –¥–ª—è Unicode).`;
        }
        
        const k = Math.floor(Math.random() * (p - 2)) + 1;
        const c1 = modPow(g, k, p);
        const s = modPow(y, k, p);
        const c2 = (m * s) % p;
        pairs.push(`${c1},${c2}`);
    }
    
    return pairs.join(' ');
}

function elgamalDecrypt(cipherPairs, p, x) {
    try {
        const pairs = cipherPairs.replace(/;/g, ' ').split(/\s+/).filter(p => p && p.includes(','));
        const result = [];
        
        let i = 0;
        while (i < pairs.length) {
            const [c1Str, c2Str] = pairs[i].split(',');
            const c1 = parseInt(c1Str);
            const c2 = parseInt(c2Str);
            
            // –í—ã—á–∏—Å–ª—è–µ–º s = c1^x mod p, –∑–∞—Ç–µ–º sInv
            const s = modPow(c1, x, p);
            const sInv = modInverse(s, p);
            
            if (sInv === null) {
                return '–û—à–∏–±–∫–∞: –Ω–µ —É–¥–∞–ª–æ—Å—å –≤—ã—á–∏—Å–ª–∏—Ç—å –æ–±—Ä–∞—Ç–Ω—ã–π —ç–ª–µ–º–µ–Ω—Ç';
            }
            
            const m = (c2 * sInv) % p;
            result.push(String.fromCharCode(m));
            i++;
        }
        
        return result.join('');
    } catch (e) {
        return `–û—à–∏–±–∫–∞: –æ–∂–∏–¥–∞—é—Ç—Å—è –ø–∞—Ä—ã –≤–∏–¥–∞ c1,c2, —Ä–∞–∑–¥–µ–ª—ë–Ω–Ω—ã–µ –ø—Ä–æ–±–µ–ª–∞–º–∏. ${e.message}`;
    }
}

// Paillier (–≥–æ–º–æ–º–æ—Ä—Ñ–Ω—ã–π –∞–ª–≥–æ—Ä–∏—Ç–º —à–∏—Ñ—Ä–æ–≤–∞–Ω–∏—è)
function paillierEncrypt(text, n, g) {
    if (n <= 2) return '–û—à–∏–±–∫–∞: n –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å > 2';
    if (n < 65536) return '–û—à–∏–±–∫–∞: n –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å >= 65536 –¥–ª—è –ø–æ–¥–¥–µ—Ä–∂–∫–∏ Unicode';
    
    const result = [];
    for (let char of text) {
        const m = char.charCodeAt(0);
        if (m >= n) {
            return '–û—à–∏–±–∫–∞: —Å–∏–º–≤–æ–ª –Ω–µ –ø–æ–º–µ—â–∞–µ—Ç—Å—è –≤ n. –í—ã–±–µ—Ä–∏—Ç–µ –±–æ–ª—å—à–µ–µ n.';
        }
        
        // –í—ã–±–∏—Ä–∞–µ–º —Å–ª—É—á–∞–π–Ω–æ–µ r
        let r;
        do {
            r = Math.floor(Math.random() * (n - 1)) + 1;
        } while (gcd(r, n) !== 1);
        
        // –®–∏—Ñ—Ä–æ–≤–∞–Ω–∏–µ: c = g^m * r^n mod n^2
        const n2 = BigInt(n) * BigInt(n);
        const g_m = modPow(g, m, Number(n2));
        const r_n = modPow(r, n, Number(n2));
        const c = Number((BigInt(g_m) * BigInt(r_n)) % n2);
        result.push(c.toString());
    }
    
    return result.join(' ');
}

function paillierDecrypt(cipherNumbers, n, lambda, mu) {
    try {
        const numbers = cipherNumbers.trim().split(/\s+/);
        const n2 = BigInt(n) * BigInt(n);
        const result = [];
        
        for (let cStr of numbers) {
            const c = BigInt(parseInt(cStr));
            
            // –î–µ—à–∏—Ñ—Ä–æ–≤–∞–Ω–∏–µ: m = L(c^lambda mod n^2) * mu mod n
            // –≥–¥–µ L(x) = (x - 1) / n
            const u = modPow(Number(c), lambda, Number(n2));
            const l = Number((BigInt(u) - 1n) / BigInt(n));
            const m = (l * mu) % n;
            result.push(String.fromCharCode(m));
        }
        
        return result.join('');
    } catch (e) {
        return `–û—à–∏–±–∫–∞: –æ–∂–∏–¥–∞–µ—Ç—Å—è –Ω–∞–±–æ—Ä —Ü–µ–ª—ã—Ö —á–∏—Å–µ–ª. ${e.message}`;
    }
}

// –í—Å–ø–æ–º–æ–≥–∞—Ç–µ–ª—å–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è –ù–û–î
function gcd(a, b) {
    a = BigInt(a);
    b = BigInt(b);
    while (b !== 0n) {
        [a, b] = [b, a % b];
    }
    return Number(a);
}

// Rabin (–∞–ª–≥–æ—Ä–∏—Ç–º –Ω–∞ –æ—Å–Ω–æ–≤–µ –∫–≤–∞–¥—Ä–∞—Ç–∏—á–Ω—ã—Ö –≤—ã—á–µ—Ç–æ–≤)
function rabinEncrypt(text, n) {
    if (n <= 2) return '–û—à–∏–±–∫–∞: n –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å > 2';
    if (n < 65536) return '–û—à–∏–±–∫–∞: n –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å >= 65536 –¥–ª—è –ø–æ–¥–¥–µ—Ä–∂–∫–∏ Unicode';
    
    const result = [];
    for (let char of text) {
        const m = char.charCodeAt(0);
        if (m >= n) {
            return '–û—à–∏–±–∫–∞: —Å–∏–º–≤–æ–ª –Ω–µ –ø–æ–º–µ—â–∞–µ—Ç—Å—è –≤ n. –í—ã–±–µ—Ä–∏—Ç–µ –±–æ–ª—å—à–µ–µ n.';
        }
        
        // –®–∏—Ñ—Ä–æ–≤–∞–Ω–∏–µ: c = m^2 mod n
        const c = modPow(m, 2, n);
        result.push(c.toString());
    }
    
    return result.join(' ');
}

function rabinDecrypt(cipherNumbers, p, q) {
    try {
        const numbers = cipherNumbers.trim().split(/\s+/);
        const n = p * q;
        const result = [];
        
        for (let cStr of numbers) {
            const c = parseInt(cStr);
            
            // –î–µ—à–∏—Ñ—Ä–æ–≤–∞–Ω–∏–µ: –Ω–∞—Ö–æ–¥–∏–º –∫–≤–∞–¥—Ä–∞—Ç–Ω—ã–µ –∫–æ—Ä–Ω–∏ –ø–æ –º–æ–¥—É–ª—é p –∏ q
            // –ò—Å–ø–æ–ª—å–∑—É–µ–º –∫–∏—Ç–∞–π—Å–∫—É—é —Ç–µ–æ—Ä–µ–º—É –æ–± –æ—Å—Ç–∞—Ç–∫–∞—Ö
            const mp = modPow(c, Math.floor((p + 1) / 4), p);
            const mq = modPow(c, Math.floor((q + 1) / 4), q);
            
            // –ö–∏—Ç–∞–π—Å–∫–∞—è —Ç–µ–æ—Ä–µ–º–∞ –æ–± –æ—Å—Ç–∞—Ç–∫–∞—Ö
            const yp = modInverse(q, p);  // q^(-1) mod p
            const yq = modInverse(p, q);  // p^(-1) mod q
            
            // –ö–∏—Ç–∞–π—Å–∫–∞—è —Ç–µ–æ—Ä–µ–º–∞ –æ–± –æ—Å—Ç–∞—Ç–∫–∞—Ö: x = mp * q * yq + mq * p * yp mod n
            const x1 = Number((BigInt(mp) * BigInt(q) * BigInt(yq) + BigInt(mq) * BigInt(p) * BigInt(yp)) % BigInt(n));
            const x2 = Number((BigInt(n) - BigInt(x1)) % BigInt(n));
            // –ê–ª—å—Ç–µ—Ä–Ω–∞—Ç–∏–≤–Ω—ã–µ –∫–æ—Ä–Ω–∏
            const mp2 = Number((BigInt(p) - BigInt(mp)) % BigInt(p));
            const mq2 = Number((BigInt(q) - BigInt(mq)) % BigInt(q));
            const x3 = Number((BigInt(mp2) * BigInt(q) * BigInt(yq) + BigInt(mq) * BigInt(p) * BigInt(yp)) % BigInt(n));
            const x4 = Number((BigInt(mp) * BigInt(q) * BigInt(yq) + BigInt(mq2) * BigInt(p) * BigInt(yp)) % BigInt(n));
            
            // –í—ã–±–∏—Ä–∞–µ–º –ø—Ä–∞–≤–∏–ª—å–Ω—ã–π –∫–æ—Ä–µ–Ω—å (–æ–±—ã—á–Ω–æ –Ω–∞–∏–º–µ–Ω—å—à–∏–π —Ä–∞–∑—É–º–Ω—ã–π —Å–∏–º–≤–æ–ª)
            const candidates = [x1, x2, x3, x4];
            const valid = candidates.filter(x => x >= 0 && x < 65536 && x === Math.floor(x));
            
            if (valid.length > 0) {
                // –í—ã–±–∏—Ä–∞–µ–º –Ω–∞–∏–º–µ–Ω—å—à–∏–π –≤–∞–ª–∏–¥–Ω—ã–π —Å–∏–º–≤–æ–ª (–æ–±—ã—á–Ω–æ —ç—Ç–æ –ø—Ä–∞–≤–∏–ª—å–Ω—ã–π)
                const minValid = Math.min(...valid);
                result.push(String.fromCharCode(minValid));
            } else {
                // –ï—Å–ª–∏ –Ω–µ –Ω–∞—à–ª–∏ –≤–∞–ª–∏–¥–Ω—ã–π, –ø—Ä–æ–±—É–µ–º x1 (–Ω–∞–∏–±–æ–ª–µ–µ –≤–µ—Ä–æ—è—Ç–Ω—ã–π)
                const fallback = (x1 >= 0 && x1 < 65536) ? x1 : (x2 >= 0 && x2 < 65536 ? x2 : 63); // 63 = '?'
                result.push(String.fromCharCode(fallback));
            }
        }
        
        return result.join('');
    } catch (e) {
        return `–û—à–∏–±–∫–∞: –æ–∂–∏–¥–∞–µ—Ç—Å—è –Ω–∞–±–æ—Ä —Ü–µ–ª—ã—Ö —á–∏—Å–µ–ª. ${e.message}`;
    }
}

// Diffie-Hellman (–∏—Å–ø—Ä–∞–≤–ª–µ–Ω–Ω—ã–π —Å –º–æ–¥—É–ª—å–Ω—ã–º –≤–æ–∑–≤–µ–¥–µ–Ω–∏–µ–º)
function dhSharedKey(p, g, a, B) {
    const K = modPow(B, a, p);
    return K.toString();
}

function dhXorEncrypt(text, p, g, a, B) {
    const key = dhSharedKey(p, g, a, B);
    return xorEncrypt(text, key);
}

function dhXorDecrypt(hexCipher, p, g, a, B) {
    const key = dhSharedKey(p, g, a, B);
    return xorDecrypt(hexCipher, key);
}

// DES (—É–ø—Ä–æ—â–µ–Ω–Ω–∞—è —É—á–µ–±–Ω–∞—è –≤–µ—Ä—Å–∏—è)
function desEncrypt(text, key) {
    if (!key || key.length < 8) return '–û—à–∏–±–∫–∞: –∫–ª—é—á –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –Ω–µ –º–µ–Ω–µ–µ 8 —Å–∏–º–≤–æ–ª–æ–≤';
    const keyBytes = new TextEncoder().encode(key.substring(0, 8));
    const textBytes = new TextEncoder().encode(text);
    const result = [];
    
    // –ü—Ä–æ—Å—Ç–∞—è –±–ª–æ—á–Ω–∞—è —Å—Ç—Ä—É–∫—Ç—É—Ä–∞ (64 –±–∏—Ç–∞ = 8 –±–∞–π—Ç)
    for (let i = 0; i < textBytes.length; i += 8) {
        const block = new Uint8Array(8);
        for (let j = 0; j < 8; j++) {
            block[j] = textBytes[i + j] || 0;
        }
        
        // –£–ø—Ä–æ—â–µ–Ω–Ω–∞—è –æ–ø–µ—Ä–∞—Ü–∏—è Feistel-–ø–æ–¥–æ–±–Ω–æ–π —Å—Ç—Ä—É–∫—Ç—É—Ä—ã
        for (let round = 0; round < 4; round++) {
            const left = block.slice(0, 4);
            const right = block.slice(4, 8);
            const keyRound = keyBytes[round % keyBytes.length];
            
            // –ü—Ä–æ—Å—Ç–∞—è —Ñ—É–Ω–∫—Ü–∏—è F
            const fResult = new Uint8Array(4);
            for (let k = 0; k < 4; k++) {
                fResult[k] = (right[k] ^ keyRound) % 256;
            }
            
            // XOR —Å –ª–µ–≤–æ–π —á–∞—Å—Ç—å—é
            const newLeft = right;
            const newRight = new Uint8Array(4);
            for (let k = 0; k < 4; k++) {
                newRight[k] = (left[k] ^ fResult[k]) % 256;
            }
            
            block.set(newLeft, 0);
            block.set(newRight, 4);
        }
        
        result.push(...Array.from(block));
    }
    
    return Array.from(result).map(b => b.toString(16).padStart(2, '0')).join('');
}

function desDecrypt(hexText, key) {
    if (!key || key.length < 8) return '–û—à–∏–±–∫–∞: –∫–ª—é—á –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –Ω–µ –º–µ–Ω–µ–µ 8 —Å–∏–º–≤–æ–ª–æ–≤';
    try {
        const bytes = new Uint8Array(hexText.match(/.{2}/g).map(byte => parseInt(byte, 16)));
        const keyBytes = new TextEncoder().encode(key.substring(0, 8));
        const result = [];
        
        for (let i = 0; i < bytes.length; i += 8) {
            const block = new Uint8Array(8);
            for (let j = 0; j < 8; j++) {
                block[j] = bytes[i + j] || 0;
            }
            
            // –û–±—Ä–∞—Ç–Ω–∞—è –æ–ø–µ—Ä–∞—Ü–∏—è (–æ–±—Ä–∞—Ç–Ω—ã–µ —Ä–∞—É–Ω–¥—ã)
            for (let round = 3; round >= 0; round--) {
                const left = block.slice(0, 4);
                const right = block.slice(4, 8);
                const keyRound = keyBytes[round % keyBytes.length];
                
                const fResult = new Uint8Array(4);
                for (let k = 0; k < 4; k++) {
                    fResult[k] = (left[k] ^ keyRound) % 256;
                }
                
                const newRight = left;
                const newLeft = new Uint8Array(4);
                for (let k = 0; k < 4; k++) {
                    newLeft[k] = (right[k] ^ fResult[k]) % 256;
                }
                
                block.set(newLeft, 0);
                block.set(newRight, 4);
            }
            
            result.push(...Array.from(block));
        }
        
        // –£–¥–∞–ª—è–µ–º –Ω—É–ª–µ–≤—ã–µ –±–∞–π—Ç—ã –≤ –∫–æ–Ω—Ü–µ
        while (result.length > 0 && result[result.length - 1] === 0) {
            result.pop();
        }
        
        return new TextDecoder().decode(new Uint8Array(result));
    } catch (e) {
        return '–û—à–∏–±–∫–∞: –æ–∂–∏–¥–∞–µ—Ç—Å—è hex-—Å—Ç—Ä–æ–∫–∞ (0-9a-f)';
    }
}

// AES (—á–µ—Ä–µ–∑ Web Crypto API)
async function aesEncrypt(text, keyStr) {
    try {
        if (!keyStr || keyStr.length < 16) return '–û—à–∏–±–∫–∞: –∫–ª—é—á –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –Ω–µ –º–µ–Ω–µ–µ 16 —Å–∏–º–≤–æ–ª–æ–≤';
        
        // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –∫–ª—é—á –≤ —Ñ–æ—Ä–º–∞—Ç –¥–ª—è AES
        const keyData = new TextEncoder().encode(keyStr.substring(0, 16).padEnd(16, '0').substring(0, 16));
        const key = await crypto.subtle.importKey(
            'raw',
            keyData,
            { name: 'AES-CBC', length: 128 },
            false,
            ['encrypt']
        );
        
        const iv = crypto.getRandomValues(new Uint8Array(16));
        const textBytes = new TextEncoder().encode(text);
        
        const encrypted = await crypto.subtle.encrypt(
            { name: 'AES-CBC', iv: iv },
            key,
            textBytes
        );
        
        // –í–æ–∑–≤—Ä–∞—â–∞–µ–º IV + –∑–∞—à–∏—Ñ—Ä–æ–≤–∞–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –≤ hex
        const result = new Uint8Array(iv.length + encrypted.byteLength);
        result.set(iv, 0);
        result.set(new Uint8Array(encrypted), iv.length);
        
        return Array.from(result).map(b => b.toString(16).padStart(2, '0')).join('');
    } catch (e) {
        return `–û—à–∏–±–∫–∞: ${e.message}`;
    }
}

async function aesDecrypt(hexText, keyStr) {
    try {
        if (!keyStr || keyStr.length < 16) return '–û—à–∏–±–∫–∞: –∫–ª—é—á –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –Ω–µ –º–µ–Ω–µ–µ 16 —Å–∏–º–≤–æ–ª–æ–≤';
        
        const bytes = new Uint8Array(hexText.match(/.{2}/g).map(byte => parseInt(byte, 16)));
        if (bytes.length < 32) return '–û—à–∏–±–∫–∞: –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ';
        
        const iv = bytes.slice(0, 16);
        const encrypted = bytes.slice(16);
        
        const keyData = new TextEncoder().encode(keyStr.substring(0, 16).padEnd(16, '0').substring(0, 16));
        const key = await crypto.subtle.importKey(
            'raw',
            keyData,
            { name: 'AES-CBC', length: 128 },
            false,
            ['decrypt']
        );
        
        const decrypted = await crypto.subtle.decrypt(
            { name: 'AES-CBC', iv: iv },
            key,
            encrypted
        );
        
        return new TextDecoder().decode(decrypted);
    } catch (e) {
        return `–û—à–∏–±–∫–∞: ${e.message}`;
    }
}

// RC4 (–ø–æ—Ç–æ–∫–æ–≤—ã–π —à–∏—Ñ—Ä)
function rc4Encrypt(text, key) {
    if (!key) return text;
    
    // KSA (Key Scheduling Algorithm)
    const S = Array.from({ length: 256 }, (_, i) => i);
    const keyBytes = new TextEncoder().encode(key);
    let j = 0;
    
    for (let i = 0; i < 256; i++) {
        j = (j + S[i] + keyBytes[i % keyBytes.length]) % 256;
        [S[i], S[j]] = [S[j], S[i]];
    }
    
    // PRGA (Pseudo-Random Generation Algorithm)
    const textBytes = new TextEncoder().encode(text);
    const result = new Uint8Array(textBytes.length);
    let i = 0;
    j = 0;
    
    for (let k = 0; k < textBytes.length; k++) {
        i = (i + 1) % 256;
        j = (j + S[i]) % 256;
        [S[i], S[j]] = [S[j], S[i]];
        const K = S[(S[i] + S[j]) % 256];
        result[k] = textBytes[k] ^ K;
    }
    
    return Array.from(result).map(b => b.toString(16).padStart(2, '0')).join('');
}

function rc4Decrypt(hexText, key) {
    if (!key) return hexText;
    try {
        const bytes = new Uint8Array(hexText.match(/.{2}/g).map(byte => parseInt(byte, 16)));
        
        // RC4 —Å–∏–º–º–µ—Ç—Ä–∏—á–µ–Ω - –¥–µ—à–∏—Ñ—Ä–æ–≤–∫–∞ –∏–¥–µ–Ω—Ç–∏—á–Ω–∞ —à–∏—Ñ—Ä–æ–≤–∞–Ω–∏—é
        const S = Array.from({ length: 256 }, (_, i) => i);
        const keyBytes = new TextEncoder().encode(key);
        let j = 0;
        
        for (let i = 0; i < 256; i++) {
            j = (j + S[i] + keyBytes[i % keyBytes.length]) % 256;
            [S[i], S[j]] = [S[j], S[i]];
        }
        
        const result = new Uint8Array(bytes.length);
        let i = 0;
        j = 0;
        
        for (let k = 0; k < bytes.length; k++) {
            i = (i + 1) % 256;
            j = (j + S[i]) % 256;
            [S[i], S[j]] = [S[j], S[i]];
            const K = S[(S[i] + S[j]) % 256];
            result[k] = bytes[k] ^ K;
        }
        
        return new TextDecoder().decode(result);
    } catch (e) {
        return '–û—à–∏–±–∫–∞: –æ–∂–∏–¥–∞–µ—Ç—Å—è hex-—Å—Ç—Ä–æ–∫–∞ (0-9a-f)';
    }
}

// Blowfish (—É–ø—Ä–æ—â–µ–Ω–Ω–∞—è —É—á–µ–±–Ω–∞—è –≤–µ—Ä—Å–∏—è)
function blowfishEncrypt(text, key) {
    if (!key || key.length < 4) return '–û—à–∏–±–∫–∞: –∫–ª—é—á –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –Ω–µ –º–µ–Ω–µ–µ 4 —Å–∏–º–≤–æ–ª–æ–≤';
    
    const keyBytes = new TextEncoder().encode(key);
    const textBytes = new TextEncoder().encode(text);
    const result = [];
    
    // –ë–ª–æ–∫–∏ –ø–æ 8 –±–∞–π—Ç (64 –±–∏—Ç–∞)
    for (let i = 0; i < textBytes.length; i += 8) {
        let left = 0, right = 0;
        
        // –§–æ—Ä–º–∏—Ä—É–µ–º 64-–±–∏—Ç–Ω—ã–π –±–ª–æ–∫
        for (let j = 0; j < 4; j++) {
            left = (left << 8) | (textBytes[i + j] || 0);
        }
        for (let j = 4; j < 8; j++) {
            right = (right << 8) | (textBytes[i + j] || 0);
        }
        
        // –£–ø—Ä–æ—â–µ–Ω–Ω—ã–π —Ä–∞—É–Ω–¥ (–¥–ª—è —É—á–µ–±–Ω—ã—Ö —Ü–µ–ª–µ–π)
        for (let round = 0; round < 4; round++) {
            const keyVal = keyBytes[round % keyBytes.length];
            const temp = left;
            left = right ^ ((left * keyVal) % 0xFFFFFFFF);
            right = temp;
        }
        
        // –ö–æ–Ω–≤–µ—Ä—Ç–∏—Ä—É–µ–º –æ–±—Ä–∞—Ç–Ω–æ –≤ –±–∞–π—Ç—ã
        const block = new Uint8Array(8);
        for (let j = 3; j >= 0; j--) {
            block[j] = left & 0xFF;
            left >>>= 8;
        }
        for (let j = 7; j >= 4; j--) {
            block[j] = right & 0xFF;
            right >>>= 8;
        }
        
        result.push(...Array.from(block));
    }
    
    return Array.from(result).map(b => b.toString(16).padStart(2, '0')).join('');
}

function blowfishDecrypt(hexText, key) {
    if (!key || key.length < 4) return '–û—à–∏–±–∫–∞: –∫–ª—é—á –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –Ω–µ –º–µ–Ω–µ–µ 4 —Å–∏–º–≤–æ–ª–æ–≤';
    try {
        const bytes = new Uint8Array(hexText.match(/.{2}/g).map(byte => parseInt(byte, 16)));
        const keyBytes = new TextEncoder().encode(key);
        const result = [];
        
        for (let i = 0; i < bytes.length; i += 8) {
            let left = 0, right = 0;
            
            for (let j = 0; j < 4; j++) {
                left = (left << 8) | (bytes[i + j] || 0);
            }
            for (let j = 4; j < 8; j++) {
                right = (right << 8) | (bytes[i + j] || 0);
            }
            
            // –û–±—Ä–∞—Ç–Ω—ã–µ —Ä–∞—É–Ω–¥—ã
            for (let round = 3; round >= 0; round--) {
                const keyVal = keyBytes[round % keyBytes.length];
                const temp = right;
                right = left ^ ((right * keyVal) % 0xFFFFFFFF);
                left = temp;
            }
            
            const block = new Uint8Array(8);
            for (let j = 3; j >= 0; j--) {
                block[j] = left & 0xFF;
                left >>>= 8;
            }
            for (let j = 7; j >= 4; j--) {
                block[j] = right & 0xFF;
                right >>>= 8;
            }
            
            result.push(...Array.from(block));
        }
        
        // –£–¥–∞–ª—è–µ–º –Ω—É–ª–µ–≤—ã–µ –±–∞–π—Ç—ã –≤ –∫–æ–Ω—Ü–µ
        while (result.length > 0 && result[result.length - 1] === 0) {
            result.pop();
        }
        
        return new TextDecoder().decode(new Uint8Array(result));
    } catch (e) {
        return '–û—à–∏–±–∫–∞: –æ–∂–∏–¥–∞–µ—Ç—Å—è hex-—Å—Ç—Ä–æ–∫–∞ (0-9a-f)';
    }
}

