// –í—ã–ø–æ–ª–Ω–∏—Ç—å —à–∏—Ñ—Ä–æ–≤–∞–Ω–∏–µ/—Ä–∞—Å—à–∏—Ñ—Ä–æ–≤–∫—É
function executeCrypto() {
    const algorithm = document.getElementById('cryptoAlgorithm').value;
    const input = document.getElementById('cryptoInput').value;
    const output = document.getElementById('cryptoOutput');
    const mode = document.getElementById('cryptoTitle').textContent === '–®–∏—Ñ—Ä–æ–≤–∞–Ω–∏–µ' ? 'encrypt' : 'decrypt';
    
    if (!input) {
        output.value = '–í–≤–µ–¥–∏—Ç–µ –¥–∞–Ω–Ω—ã–µ –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏';
        return;
    }
    
    let result = '';
    
    try {
        if (algorithm === 'caesar') {
            const shift = parseInt(document.getElementById('caesarShift').value);
            result = caesarCipher(input, mode === 'encrypt' ? shift : -shift);
        } else if (algorithm === 'vigenere') {
            const key = document.getElementById('vigenereKey').value;
            result = vigenereCipher(input, key, mode === 'decrypt');
        } else if (algorithm === 'xor') {
            const key = document.getElementById('xorKey').value;
            if (mode === 'encrypt') {
                result = xorEncrypt(input, key);
            } else {
                result = xorDecrypt(input, key);
            }
        } else if (algorithm === 'des') {
            const key = document.getElementById('desKey').value;
            if (mode === 'encrypt') {
                result = desEncrypt(input, key);
            } else {
                result = desDecrypt(input, key);
            }
        } else if (algorithm === 'aes') {
            const key = document.getElementById('aesKey').value;
            // AES –∏—Å–ø–æ–ª—å–∑—É–µ—Ç async —Ñ—É–Ω–∫—Ü–∏–∏, –Ω—É–∂–Ω–æ –æ–±—Ä–∞–±–æ—Ç–∞—Ç—å —á–µ—Ä–µ–∑ Promise
            (async () => {
                if (mode === 'encrypt') {
                    result = await aesEncrypt(input, key);
                } else {
                    result = await aesDecrypt(input, key);
                }
                output.value = result;
            })();
            return; // –í—ã—Ö–æ–¥–∏–º —Ä–∞–Ω—å—à–µ, —Ç–∞–∫ –∫–∞–∫ —Ä–µ–∑—É–ª—å—Ç–∞—Ç –±—É–¥–µ—Ç —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω –∞—Å–∏–Ω—Ö—Ä–æ–Ω–Ω–æ
        } else if (algorithm === 'rc4') {
            const key = document.getElementById('rc4Key').value;
            if (mode === 'encrypt') {
                result = rc4Encrypt(input, key);
            } else {
                result = rc4Decrypt(input, key);
            }
        } else if (algorithm === 'blowfish') {
            const key = document.getElementById('blowfishKey').value;
            if (mode === 'encrypt') {
                result = blowfishEncrypt(input, key);
            } else {
                result = blowfishDecrypt(input, key);
            }
        } else if (algorithm === 'rsa') {
            if (mode === 'encrypt') {
                const e = parseInt(document.getElementById('rsaE').value);
                const n = parseInt(document.getElementById('rsaN').value);
                result = rsaEncrypt(input, e, n);
            } else {
                const d = parseInt(document.getElementById('rsaD').value);
                const n = parseInt(document.getElementById('rsaN').value);
                result = rsaDecrypt(input, d, n);
            }
        } else if (algorithm === 'elgamal') {
            if (mode === 'encrypt') {
                const p = parseInt(document.getElementById('elgamalP').value);
                const g = parseInt(document.getElementById('elgamalG').value);
                const y = parseInt(document.getElementById('elgamalY').value);
                result = elgamalEncrypt(input, p, g, y);
            } else {
                const p = parseInt(document.getElementById('elgamalP').value);
                const x = parseInt(document.getElementById('elgamalX').value);
                result = elgamalDecrypt(input, p, x);
            }
        } else if (algorithm === 'paillier') {
            if (mode === 'encrypt') {
                const n = parseInt(document.getElementById('paillierN').value);
                const g = parseInt(document.getElementById('paillierG').value);
                result = paillierEncrypt(input, n, g);
            } else {
                const n = parseInt(document.getElementById('paillierN').value);
                const lambda = parseInt(document.getElementById('paillierLambda').value);
                const mu = parseInt(document.getElementById('paillierMu').value);
                result = paillierDecrypt(input, n, lambda, mu);
            }
        } else if (algorithm === 'rabin') {
            if (mode === 'encrypt') {
                const n = parseInt(document.getElementById('rabinN').value);
                result = rabinEncrypt(input, n);
            } else {
                const p = parseInt(document.getElementById('rabinP').value);
                const q = parseInt(document.getElementById('rabinQ').value);
                result = rabinDecrypt(input, p, q);
            }
        } else if (algorithm === 'dh') {
            const p = parseInt(document.getElementById('dhP').value);
            const g = parseInt(document.getElementById('dhG').value);
            const a = parseInt(document.getElementById('dhA').value);
            const B = parseInt(document.getElementById('dhB').value);
            if (mode === 'encrypt') {
                result = dhXorEncrypt(input, p, g, a, B);
            } else {
                result = dhXorDecrypt(input, p, g, a, B);
            }
        }
        
        output.value = result;
    } catch (e) {
        output.value = `–û—à–∏–±–∫–∞: ${e.message}`;
    }
}

// –ü–æ–∫–∞–∑–∞—Ç—å –º–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ —Ç–µ–æ—Ä–∏–∏
