// –û—Ç–ø—Ä–∞–≤–∏—Ç—å —Ç–µ—Å—Ç
function submitTest() {
    let score = 0;
    const selectedQuestions = window.currentTestQuestions || [];
    const totalQuestions = selectedQuestions.length;
    const wrongAnswers = [];
    
    if (totalQuestions === 0) {
        alert('–ù–µ—Ç –≤–æ–ø—Ä–æ—Å–æ–≤ –¥–ª—è –ø—Ä–æ–≤–µ—Ä–∫–∏');
        return;
    }
    
    selectedQuestions.forEach((question, index) => {
        const selectedOption = document.querySelector(`input[name="question${question.id}"]:checked`);
        if (selectedOption) {
            const selectedIndex = parseInt(selectedOption.value);
            if (selectedIndex === question.correct) {
                score++;
            } else {
                wrongAnswers.push(index + 1);
            }
        } else {
            wrongAnswers.push(index + 1);
        }
    });
    
    const percentage = Math.round((score / totalQuestions) * 100);
    const grade = getGrade(percentage);
    
    // –°–æ—Ö—Ä–∞–Ω–∏—Ç—å –æ—Ü–µ–Ω–∫—É
    if (!grades[currentUser.username]) {
        grades[currentUser.username] = [];
    }
    
    const testType = window.currentTestCategory === 'symmetric' ? '–°–∏–º–º–µ—Ç—Ä–∏—á–Ω—ã–µ –∞–ª–≥–æ—Ä–∏—Ç–º—ã' : '–ê—Å–∏–º–º–µ—Ç—Ä–∏—á–Ω—ã–µ –∞–ª–≥–æ—Ä–∏—Ç–º—ã';
    
    grades[currentUser.username].push({
        date: new Date().toLocaleDateString(),
        score: score,
        total: totalQuestions,
        percentage: percentage,
        grade: grade,
        testType: testType
    });
    
    saveData();
    
    // –ü–æ–∫–∞–∑–∞—Ç—å —Ä–µ–∑—É–ª—å—Ç–∞—Ç
    const resultDiv = document.createElement('div');
    resultDiv.className = 'test-result';
    resultDiv.innerHTML = `
        <h3>–†–µ–∑—É–ª—å—Ç–∞—Ç —Ç–µ—Å—Ç–∞</h3>
        <p><strong>–¢–∏–ø —Ç–µ—Å—Ç–∞:</strong> ${testType}</p>
        <p><strong>–ü—Ä–∞–≤–∏–ª—å–Ω—ã—Ö –æ—Ç–≤–µ—Ç–æ–≤:</strong> ${score} –∏–∑ ${totalQuestions}</p>
        <p><strong>–ü—Ä–æ—Ü–µ–Ω—Ç:</strong> ${percentage}%</p>
        <p><strong>–û—Ü–µ–Ω–∫–∞:</strong> ${grade}</p>
        ${wrongAnswers.length > 0 ? `<p><strong>–û—à–∏–±–∫–∏ –≤ –≤–æ–ø—Ä–æ—Å–∞—Ö:</strong> ${wrongAnswers.join(', ')}</p>` : ''}
    `;
    
    document.getElementById('testContent').appendChild(resultDiv);
    document.getElementById('submitTest').style.display = 'none';
}

// –ü–æ–ª—É—á–∏—Ç—å –æ—Ü–µ–Ω–∫—É –ø–æ –ø—Ä–æ—Ü–µ–Ω—Ç—É
function getGrade(percentage) {
    if (percentage >= 90) return '–û—Ç–ª–∏—á–Ω–æ (5)';
    if (percentage >= 80) return '–•–æ—Ä–æ—à–æ (4)';
    if (percentage >= 70) return '–£–¥–æ–≤–ª–µ—Ç–≤–æ—Ä–∏—Ç–µ–ª—å–Ω–æ (3)';
    return '–ù–µ—É–¥–æ–≤–ª–µ—Ç–≤–æ—Ä–∏—Ç–µ–ª—å–Ω–æ (2)';
}

