// –ü–æ–ª—É—á–∏—Ç—å –∫–ª–∞—Å—Å –æ—Ü–µ–Ω–∫–∏
function getGradeClass(percentage) {
    if (percentage >= 90) return 'excellent';
    if (percentage >= 80) return 'good';
    if (percentage >= 70) return 'average';
    return 'poor';
}

