// –ü–æ–∫–∞–∑–∞—Ç—å –º–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ —Å–º–µ–Ω—ã –ø–∞—Ä–æ–ª—è
function showChangePasswordModal() {
    const modalBody = document.getElementById('modalBody');
    modalBody.innerHTML = `
        <h3>–°–º–µ–Ω–∏—Ç—å –ø–∞—Ä–æ–ª—å</h3>
        <form id="changePasswordForm">
            <div class="form-group">
                <label for="oldPassword">–°—Ç–∞—Ä—ã–π –ø–∞—Ä–æ–ª—å:</label>
                <input type="password" id="oldPassword" required>
            </div>
            <div class="form-group">
                <label for="newPassword1">–ù–æ–≤—ã–π –ø–∞—Ä–æ–ª—å:</label>
                <input type="password" id="newPassword1" required>
            </div>
            <div class="form-group">
                <label for="newPassword2">–ü–æ–≤—Ç–æ—Ä–∏—Ç–µ –ø–∞—Ä–æ–ª—å:</label>
                <input type="password" id="newPassword2" required>
            </div>
            <button type="submit" class="btn btn-warning">–ò–∑–º–µ–Ω–∏—Ç—å</button>
        </form>
    `;
    
    document.getElementById('changePasswordForm').addEventListener('submit', function(e) {
        e.preventDefault();
        const oldPassword = document.getElementById('oldPassword').value;
        const newPassword1 = document.getElementById('newPassword1').value;
        const newPassword2 = document.getElementById('newPassword2').value;
        
        if (oldPassword !== currentUser.password) {
            alert('–ù–µ–≤–µ—Ä–Ω—ã–π —Å—Ç–∞—Ä—ã–π –ø–∞—Ä–æ–ª—å');
            return;
        }
        
        if (newPassword1 !== newPassword2) {
            alert('–ü–∞—Ä–æ–ª–∏ –Ω–µ —Å–æ–≤–ø–∞–¥–∞—é—Ç');
            return;
        }
        
        const missing = validatePassword(newPassword1);
        if (missing.length > 0) {
            alert('–ü–∞—Ä–æ–ª—å –Ω–µ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É–µ—Ç —Ç—Ä–µ–±–æ–≤–∞–Ω–∏—è–º:\n' + missing.join('\n'));
            return;
        }
        
        currentUser.password = newPassword1;
        users[currentUser.username].password = newPassword1;
        saveData();
        alert('–ü–∞—Ä–æ–ª—å –∏–∑–º–µ–Ω–µ–Ω');
        closeModal();
    });
    
    showModal('modal');
}

